#!/bin/bash
#SBATCH --account EcoGenetics
#SBATCH --partition normal
#SBATCH --mem-per-cpu 12G
#SBATCH --cpus-per-task 6
#SBATCH --time 1-10:00:00

awk \
'BEGIN{OFS="\t"}
{if ($0 ~ /^#/)
	{next}
else
	{for(i=10;i<=NF;i++)
		if (substr($i,length($i)-2,3)=="0.0") {sub($i, "0", $i)} else {sub($i,substr($i,length($i)-3,4), $i)}
	}; print
}	
' INPUT_freebayes.vcf > out

sed -i 's/:.:./10/g' out

sed -i 's/://g' out

awk \
'BEGIN{OFS="\t"}
	{for(i=10;i<=NF;i++) 
		sub($i, $i*100, $i)
	; print $0}
' out  > out1

grep '#CHROM' /faststorage/project/EcoGenetics/people/Jesper_Bechsgaard/temp/EntNic_biallelic_cnt2_200_neutral_norepeat.recode.vcf > header
cat header out1 > out2
awk 'BEGIN { FS="\t"; OFS="\t" } {$1=$2=$3=$4=$5=$6=$7=$8=$9="";gsub(",+",",",$0)}1' out2 > out3
sed -i 's/\t\+/\t/g;s/^\t//' out3
awk 'NR==1{for(i=1;i<=NF;i++) printf $i" "; print ""}' out3 > populations
awk '{OFS=RS;$1=$1}1' populations > pop

sed -i '1d' out3

awk \
'BEGIN{FS=OFS="\t"}
{for(i=1;i<=NF;i++)
	{if ($i > 50) 
		{sub($i,100 - $i, $i)}
	}; print
}
' out3 > out4

